                                </label>
                              </td>
                              <td 
                                className={classes.cell16} 
                                style={{textAlign:"center"}}>
                                <div>
                                  <label 
                                    id="labelr" 
                                    className={classes.RadioButton}>
                                    <input type="radio" 
                                      name="Testradio" 
                                      id="RadioBtn"
                                      value="false"
                                      onChange={this.handleRadiobtn}  
                                       />
                                    <span 
                                      className={classes.checkmark}>
                                    </span>
                                  </label>
                                    <label 
                                      id="labelr" 
                                      className={classes.RadioButton}>
                                    <input 
                                      type="radio" 
                                      name="Testradio" 
                                      id="RadioBtn2" 
                                      value="true"
                                      onChange={this.handleRadiobtn}
                                      />                                  
                                    <span 
                                      className={classes.checkmark}>  
                                    </span>
                                  </label>
                                </div>
                              </td>
                            </tr>
                          </table>
                          <table>
                          <tr>
                            <td 
                              id={classes.cell1}  
                              style={{textAlign:"right"}}>
                              <label 
                                htmlFor="frameInterval" 
                                style={labelstyle}>
                                Frame Interval:
                              </label>
                            </td>
                            <td 
                              className={classes.cell2} 
                              style={{textAlign:"left", width:"30%"}}>
                              <input 
                                // onChangeText={(text) => {this.setState({ frameinterval: text })}} 
                                style={textbxstyle} 
                                type="text" 
                                id="frameinterval" 
                                name="frameinterval"
                                value={this.state.frameInterval}
                                onChange={this.handleFrameIChange}/>
                            </td>
                          </tr>
                          <tr>
                            <td 
                              className={classes.cell1} 
                              style={{textAlign:"right"}}>
                              <label 
                                htmlFor="anomalyDuration" 
                                style={labelstyle}>
                                Anomaly Duration:
                              </label>
                            </td>
                            <td 
                              className={classes.cell2} 
                              style={{textAlign:"left"}}>
                              <input 
                                // onChangeText={(text) => {this.setState({ anomalyduration: text })}} 
                                style={textbxstyle} 
                                type="text" 
                                id="anomalyduration" 
                                name="anomalyduration"
                                value={this.state.anomalyDuration}
                                onChange={this.handleAnomDChange}/>
                            </td>
                          </tr>
                          <tr>
                            <td 
                              className={classes.cell1} 
                              style={{textAlign:"right"}}>
                              <label 
                                htmlFor="smoothingFrame" 
                                style={labelstyle}>
                                Smoothing Frame:
                              </label>
                            </td>
                            <td 
                              className={classes.cell2} 
                              style={{textAlign:"left"}}>
                              <input 
                                // onChangeText={(text) => {this.setState({ smoothingFrame: text })}} 
                                style={textbxstyle} 
                                type="text" 
                                id="smoothingframe" 
                                name="smoothingframe"
                                value={this.state.smoothingFrame}
                                onChange={this.handleSmoothChange}/>
                            </td>
                          </tr>
                          </table>
                        </div>
                      </Accordion3>
                        <div className={classes.flexChild}>
                          <table>
                            <tr>
                              <td 
                                className={classes.cell7} 
                                style={{textAlign:"right", paddingRight: "2%", paddingTop:"2%"}}>
                                <input 
                                  type="submit" value="Submit" 
                                  style={buttonstyle}/>
                              </td>
                            </tr>
                          </table>
                        </div>
            </fieldset>
          </form>
        </main>
      </div>
    );
  }
}

