import React, {Component} from 'react';
import {withRouter} from 'react-router-dom';
import axios from '../../axios';

import classes from './OrgForm.module.css';
import RadioButton from '../../components/Form Inputs/RadioButton/RadioButton';
import Heading2 from '../../components/Form Inputs/Heading2/Heading2';
import Accordion from '../../components/Accordion/Accordion';
import Accordion2 from '../../components/Accordion/Accordion2';
import Accordion3 from '../../components/Accordion/Accordion3';
import Accordion4 from '../../components/Accordion/Accordion4';
import Accordion5 from '../../components/Accordion/Accodion5';

class OrgForm extends Component {
  constructor() {
    super();
    this.state = {
          name: '',
          ID: '',
          typeofaddress:'',
          street:'',
          city:'',
          statea:'',
          zipcode:'',
          country:'',
          typeofaddress2:'',
          street2:'',
          city2:'',
          statea2:'',
          zipcode2:'',
          country2:'',
          typeofcontact:'',
          fname:'',
          lname:'',
          phone:'',
          email:'',
          typeofcontact2:'',
          fname2:'',
          lname2:'',
          phone2:'',
          email2:'',
          imagecapture:'',
          frameinterval:'',
          anomalyduration:'',
          smoothingframe:'',
          emailError: ''
    }

    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleOrgIDChange = this.handleOrgIDChange.bind(this);
    this.handleTofAChange = this.handleTofAChange.bind(this);
    this.handleStrChange = this.handleStrChange.bind(this);
    this.handleCityChange = this.handleCityChange.bind(this);
    this.handleStateChange = this.handleStateChange.bind(this);
    this.handleZipChange = this.handleZipChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  // whenever a change is made to an input field, update the corresponding state property
  handleNameChange(event) {
    this.setState({name: event.target.value});
  }

  handleOrgIDChange(event) {
    this.setState({OrgID: event.target.value});
  }

  handleTofAChange(event) {
    this.setState({typeofaddress: event.target.value});
  }

  handleStrChange(event) {
    this.setState({street: event.target.value});
  }

  handleCityChange(event) {
    this.setState({city: event.target.value});
  }

  handleStateChange(event) {
    this.setState({statea: event.target.value});
  }

  handleZipChange(event) {
    this.setState({zipcode: event.target.value});
  }

  handleZipChange(event) {
    this.setState({zipcode: event.target.value});
  }

  handleSubmit(event) {
    // prevent default submit action
    event.preventDefault();
    // distribute data stored in state into new object
    const formData = {...this.state}

    // make api call to post data
    axios.post('/orgs', formData)
         .then(response => console.log(response))
         .catch(error => console.log(error));

    //redirect to confirmation page
    this.props.history.push('/add-test-confirmation')
  }
      state = {
      isOpen: true,
      isOpen2: false,
      isOpen3: false,
      isOpen4: false, 
      isOpen5: false,
      };

      onChange = isOpen => {
        this.setState({
          isOpen
        });
      };

      onChange2 = isOpen2 => {
        this.setState({
          isOpen2
        });
      };

      onChange3 = isOpen3 => {
        this.setState({
          isOpen3
        });
      };

      onChange4 = isOpen4 => {
        this.setState({
          isOpen4
        });
      };

      onChange5 = isOpen5 => {
        this.setState({
          isOpen5
        });
      };


  render () {
    const labelstyle = {
      color: "#3cB650",
	    fontFamily: "sans-serif",
      fontWeight: "400",
      padding: "20px",
      position: "relative",
      textRendering: "optimizeLegibility",
      display: "inline-block",
      width: "70%"
      };
    
      const textbxstyle = {
        width: "70%",
        padding: "12px 20px",
        margin: "8px 0",
        border: "1px solid #ccc",
        borderRadius: "4px",
        boxSizing: "border-box",
        display: "inline-block"
      }; 

      const textbxstyle2 = {
        width: "70%",
        padding: "12px 20px",
        margin: "8px 0",
        border: "1px solid #ccc",
        borderRadius: "4px",
        boxSizing: "border-box",
        display: "inline-block"
      }; 

      const buttonstyle = {
        backgroundColor: "#4CAF50",
        border: "none",
        color: "white",
        padding: "10px 22px",
        textAlign: "center",
        textDecoration: "none",
        display: "inline-block",
        fontSize: "16px",
        margin: "4px 2px",
        cursor: "pointer",
        borderRadius:"30px",
      };

      // const textbxstyle3 = {
      //   width: "90%",
      //   padding: "12px 20px",
      //   margin: "8px 0",
      //   border: "1px solid #ccc",
      //   borderRadius: "4px",
      //   boxSizing: "border-box",
      //   display: "inline-block"
      // }; 

      // const textbxstyle4 = {
      //   width: "40%",
      //   padding: "12px 20px",
      //   margin: "8px 0",
      //   border: "1px solid #ccc",
      //   borderRadius: "4px",
      //   boxSizing: "border-box",
      //   display: "inline-block"
      // }; 

      const { isOpen } = this.state;
      const { isOpen2 } = this.state;
      const { isOpen3 } = this.state;
      const { isOpen4 } = this.state;
      const { isOpen5 } = this.state;
      
    return (
      <div>
        <main className={classes.main}>
          <Heading2>Add Organizations</Heading2>
          <form className={classes.wrapper} onSubmit={this.handleSubmit}>
            <fieldset>
              <div className={classes.flexChild}>
                <table>
                  <tr>
                    <td className={classes.cell3} 
                        style={{textAlign:"right"}}>
                        <label htmlFor="orgname" 
                          style={labelstyle}>
                          Name:
                       </label>
                    </td>
                    <td 
                      className={classes.cell4} 
                      style={{textAlign:"left"}}>
                      <input 
                        // onBlur = {()=> this.ValidatorN()} 
                        // onChangeText={(text) => {this.setState({ name: text })}} 
                        style={textbxstyle2} 
                        type="text" 
                        id="orgname" 
                        name="orgname"
                        value={this.state.name}
                        onChange={this.handleNameChange}
                        />
                      {/* <p 
                        style={{color:"red"}}>
                        {this.state.emptyErrorName}
                      </p> */}
                    </td>
                  </tr>
                  <tr>
                    <td 
                      className={classes.cell3} 
                      style={{textAlign:"right"}}>
                      <label 
                        htmlFor="orgID" 
                        style={labelstyle}>
                        ID:
                      </label>
                    </td>
                    <td 
                      className={classes.cell4} 
                      style={{textAlign:"left"}}>
                        <input 
                          // onChangeText={(text) => {this.setState({ ID: text })}} 
                          style={textbxstyle2} 
                          type="text" 
                          id="orgID" 
                          name="orgID"
                          value={this.state.ID}
                          onChange={this.handleorgIDChange}/>
                    </td>
                  </tr>
                </table>
                  <hr></hr>
                    </div> 
                      <Accordion isOpen={isOpen} onChange={this.onChange} label={"Address"}>
                        <div>
                          <table>
                            <tr>
                              <td 
                                className={classes.cell3} 
                                style={{textAlign:"right"}}>
                                <label for="addresstype" 
                                  style={labelstyle}>
                                  Type of Address (Primary/Billing/Technical):
                                </label>
                              </td>
                              <td 
                                className={classes.cell4}
                                style={{textAlign:"left"}}>
                                <input 
                                onBlur = {()=> this.ValidatorTofA()} 
                                style={textbxstyle2} 
                                onChangeText={(text) => {this.setState({ typeofaddress: text })}} 
                                type="text" 
                                id="addresstype" 
                                name="addresstype"
                                value={this.state.typeofaddress}
                                onChange={this.handleTofAChange}/>
                                {/* <p style={{color:"red"}}>
                                  {this.state.emptyErrorTofA}
                                </p> */}
                              </td>
                            </tr>
                            <tr>
                              <td 
                                className={classes.cell3} 
                                style={{textAlign:"right"}}>
                                <label 
                                  htmlFor="street" 
                                  style={labelstyle}>
                                  Street:
                                </label>
                              </td>
                              <td 
                                className={classes.cell4} 
                                style={{textAlign:"left"}}>
                                <input 
                                  // onBlur = {()=> this.ValidatorStr()} 
                                  // onChangeText={(text) => {this.setState({ street: text })}}
                                  style={textbxstyle2} 
                                  type="text" 
                                  id="street" 
                                  name="street"
                                  value={this.state.street}
                                  onChange={this.handleStrChange}
                                  />
                                <p 
                                  style={{color:"red"}}>
                                  {this.state.emptyErrorStreet}
                                </p>
                              </td>
                            </tr>
                            <tr>
                              <td 
                                className={classes.cell3} 
                                style={{textAlign:"right"}}>
                                <label 
                                  htmlFor="city" 
                                  style={labelstyle}>
                                  City:
                                </label>
                              </td>
                              <td 
                                className={classes.cell4} 
                                style={{textAlign:"left"}}>
                                <input 
                                  // onBlur = {()=> this.ValidatorCit()} 
                                  // onChangeText={(text) => {this.setState({ city: text })}} 
                                  style={textbxstyle2} 
                                  type="text" 
                                  id="city" 
                                  name="city"
                                  value={this.state.city}
                                  onChange={this.handleCityChange}
                                  />
                                  {/* <p style={{color:"red"}}>
                                    {this.state.emptyErrorCity}
                                </p> */}
                              </td>
                            </tr>
                            <tr> 
                              <td 
                                className={classes.cell3} 
                                style={{textAlign:"right"}}>
                                <label 
                                  htmlFor="state" 
                                  style={labelstyle}>
                                  State/Province:
                                </label>
                              </td>
                              <td 
                                className={classes.cell4} 
                                style={{textAlign:"left"}}>
                                <input 
                                  // onBlur = {()=> this.ValidatorSt()} 
                                  style={textbxstyle2} 
                                  // onChangeText={(text) => {this.setState({ statea: text })}} 
                                  type="text" 
                                  id="state" 
                                  name="state"
                                  value={this.state.statea}
                                  onChange={this.handleStateChange}/>
                                {/* <p style={{color:"red"}}>
                                  {this.state.emptyErrorState}
                                </p> */}
                              </td>
                            </tr>
                            <tr>
                              <td 
                                className={classes.cell3} 
                                style={{textAlign:"right"}}>
                                <label 
                                  htmlFor="zip" 
                                  style={labelstyle}>
                                  Zip Code:
                                </label>
                              </td>
                              <td 
                                className={classes.cell4} 
                                style={{textAlign:"left"}}>
                                <input 
                                  // onBlur = {()=> this.ValidatorZC()} 
                                  style={textbxstyle2} 
                                  // onChangeText={(text) => {this.setState({ zipcode: text })}} 
                                  type="text" 
                                  id="zip" 
                                  name="zip"
                                  value={this.state.zipcode}
                                  onChange={this.handleZipChange}/>
                                {/* <p style={{color:"red"}}>
                                  {this.state.emptyErrorZipcode}
                                </p> */}
                              </td>
                            </tr>
                            <tr>
                              <td 
                                className={classes.cell3} 
                                style={{textAlign:"right"}}>
                                <label 
                                htmlFor="country" 
                                style={labelstyle}>
                                  Country:
                                </label>
                              </td>
                              <td 
                                className={classes.cell4} 
                                style={{textAlign:"left"}}>
                                <input 
                                // onBlur = {()=> this.ValidatorCountry()} 
                                style={textbxstyle2} 
                                // onChangeText={(text) => {this.setState({ country: text })}}
                                type="text" 
                                id="country" 
                                name="country"
                                value={this.state.country}
                                onChange={this.handleCountryChange}/>
                                {/* <p style={{color:"red"}}>
                                  {this.state.emptyErrorCountry}
                                </p> */}
                              </td>
                            </tr>
                          </table> 
                        </div>
                        <Accordion4 isOpen4={isOpen4} onChange={this.onChange4} label={"Add Additional Address"}>
                        <div>
                          <table>
                            <tr>
                              <td 
                                className={classes.cell3} 
                                style={{textAlign:"right"}}>
                                <label 
                                  htmlFor="addresstype2" 
                                  style={labelstyle}>
                                  Type of Address (Primary/Billing/Technical):
                                </label>
                              </td>
                              <td 
                                className={classes.cell4} 
                                style={{textAlign:"left"}}>
                                <input 
                                  onChangeText={(text) => {this.setState({ typeofaddress2: text })}} 
                                  style={textbxstyle2} 
                                  type="text" 
                                  id="addresstype2" 
                                  name="addresstype2"/>
                              </td>
                            </tr>
                            <tr>
                              <td 
                                className={classes.cell3} 
                                style={{textAlign:"right"}}>
                                <label 
                                  htmlFor="street2" 
                                  style={labelstyle}>
                                  Street:
                                </label>
                              </td>
                              <td 
                                className={classes.cell4} 
                                style={{textAlign:"left"}}>
                                <input 
                                  style={textbxstyle2} 
                                  onChangeText={(text) => {this.setState({ street2: text })}}
                                  type="text" 
                                  id="street2" 
                                  name="street2"/>
                              </td>
                            </tr>
                            <tr>
                              <td 
                                className={classes.cell3} 
                                style={{textAlign:"right"}}>
                                <label 
                                  htmlFor="city2" 
                                  style={labelstyle}>
                                  City:
                                </label>
                              </td>
                              <td 
                                className={classes.cell4} 
                                style={{textAlign:"left"}}>
                                <input 
                                  style={textbxstyle2} 
                                  onChangeText={(text) => {this.setState({ city2: text })}} 
                                  type="text" 
                                  id="city2" 
                                  name="city2"/>
                              </td>
                            </tr>
                            <tr>
                              <td className={classes.cell3} 
                                style={{textAlign:"right"}}>
                                <label 
                                  htmlFor="state2" 
                                  style={labelstyle}>
                                  State/Province:
                                </label>
