import React, {Component} from 'react';

import classes from './LoginPage.module.css';
import NavBar from '../../components/NavBar/NavBar';
import Footer from '../../components/Footer/Footer';
import TextBox from '../../components/Form Inputs/TextBox/TextBox';
import FormLabel from '../../components/Form Inputs/FormLabel/FormLabel';
import DropDown from '../../components/Form Inputs/DropDown/DropDown';
import RadioButton from '../../components/Form Inputs/RadioButton/RadioButton';
import Button from '../../components/UI/Button/Button'; 
import FormContainer from '../../containers/FormContainer/FormContainer';
import Heading1 from '../../components/Heading1/Heading1';
import Heading2 from '../../components/Form Inputs/Heading2/Heading2';

class LoginPage extends Component {
  buttonClickedHandler = () => {
    alert('You clicked a button');
  }

  render () {
    const labelstyle = {
      color: "#3cB650",
	    fontFamily: "sans-serif",
	    fontWeight: "400",
    	overflowX: "hidden",
      textRendering: "optimizeLegibility",
      padding: "20px",
      marginRight: "1em",
      textAlign: "left"
      };
    
      const textbxstyle = {
        width: "50%",
        padding: "12px 20px",
        margin: "8px 0",
        border: "1px solid #ccc",
        borderRadius: "4px",
        boxSizing: "border-box",
      };


    return (
      <div>
        <NavBar />
        <main className={classes.main}>
          <FormContainer> 
              <Heading2>Log In</Heading2><br/>
            <ul>
              <li class="form-row">
              <label for="email" style={labelstyle}>Email:</label> <input style={textbxstyle} type="text" id="eml" name="eml"/>
              </li>
              <li class="form-row">
              <label for="password"style={labelstyle} >Password:</label> <input style={textbxstyle} type="text" id="pssword" name="pssword"/>
              </li>
            </ul>
              <Button>Submit</Button>
          </FormContainer>
        </main>
        <Footer />
      </div>
    );
  }
}

export default LoginPage;
