                                style={{textAlign:"right", paddingRight: "2%", paddingTop:"2%"}}>
                                <input
                                  //onClick={this.stateUpdate}
                                  type="submit" value="Submit" 
                                  style={buttonstyle}/>
                              </td>
                            </tr>
                          </table>
                        </div>
            </fieldset>
          </form>
        </main>
      </div>
    );
  }
}

export default withRouter(EditOrgForm);
