import React, {Component} from 'react';

import classes from './LoginPage.module.css';
import Button from '../../components/UI/Button/Button'; 
import FormContainer from '../../containers/FormContainer/FormContainer';
import Heading2 from '../../components/Form Inputs/Heading2/Heading2';
import LoginLink from '../../components/NavBar/LoginLink/LoginLink'

class LoginPage extends Component {
  constructor() {
    super();
    


    this.emailVal = this.emailVal.bind(this);
    this.passwordVal = this.passwordVal.bind(this);
  }
  emailVal(event) {
    let emailv = document.getElementById("eml")
    let rg = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    let isValid= rg.test(emailv.value)
    
    if (event.target.value === ""){
      document.getElementById("emailErrMsg").innerHTML="Required"
      }
    else if (!isValid) {
        document.getElementById("emailErrMsg").innerHTML="The email has an incorrect format"
      }
    else {
        document.getElementById("emailErrMsg").innerHTML=""
      }
  }

  passwordVal(event) {
    if (event.target.value === ""){
      document.getElementById("passwordErrMsg").innerHTML="Required"
      }
      else{
      document.getElementById("passwordErrMsg").innerHTML=""
      }
  }


  render () {
    const labelstyle = {
      color: "#3cB650",
	    fontFamily: "sans-serif",
      fontWeight: "400",
      padding: "20px",
      position: "relative",
      textRendering: "optimizeLegibility",
      display: "inline-block",
      textAlign: "right"
      };
    
      const textbxstyle = {
        width: "50%",
        padding: "12px 20px",
        margin: "8px 0",
        border: "1px solid #ccc",
        borderRadius: "4px",
        boxSizing: "border-box",
        display: "inline-block"
      }; 

    return (
      <main className={classes.main}>
        <form className={classes.wrapper} 
            onSubmit={this.handleSubmit}>
            <fieldset>
            <Heading2>Log In</Heading2>
            <table>
              <tr>
                <td 
                  id={classes.cell} 
                  style={{textAlign:"right"}} >
                  <label 
                    htmlFor="email" 
                    style={labelstyle} >
                    Email:
                  </label>
                </td>
                <td 
                  className={classes.cell2} 
                  style={{textAlign:"left"}}>
                  <input 
                    style={textbxstyle} 
                    type="text" 
                    id="eml" 
                    name="eml"
                    onBlur={this.emailVal}/>
                    <br/>
                    <span 
                      id="emailErrMsg"
                      style={{color:"red"}}>
                    </span>
                </td>
            </tr>
            <tr>
              <td 
                className={classes.cell} 
                style={{textAlign:"right"}}>
                <label 
                  htmlFor="pssword" 
                  style={labelstyle}>
                  Password:
                </label>
              </td>
              <td 
                className={classes.cell2} 
                style={{textAlign:"left"}}>
                <input 
                  style={textbxstyle} 
                  type="text" 
                  id="pssword" 
                  name="pssword"
                  onBlur={this.passwordVal}/>
                  <br/>
                    <span 
                      id="passwordErrMsg"
                      style={{color:"red"}}>
                  </span>
              </td>
            </tr>
            </table> 
            <br/>
            <Button>Submit</Button> 
            <br/>
            <LoginLink>Forgot Password?</LoginLink>
        </fieldset>
        </form>
      </main>
    );
  }
}

export default LoginPage;
